<?phpinclude_once(PATH_CONT.'C_Base.php');//// Конттроллер страницы чтения.//class C_Error404 extends C_Base{	//	// Конструктор.	//	function __construct()	{			}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()	{		parent::OnInput();		$this->title = $this->title . ' :: 404.';	}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$vars = array();			$this->content = $this->View(PATH_VIEW.'v_404.php', $vars);		parent::OnOutput();	}	}