<?phpinclude_once(PATH_CONT.'Controller_Base.php');class Controller_Api extends Controller{	function action_index(){				include PATH_MODL."Model_Gallery.php";		$model  = Model_Gallery::Instance();		if ($this->IsGet() && isset($_GET['id']) && !empty($_GET['id']))		{					$id = $this->preparation($_GET['id'],"number");				if (isset($id) && !empty($id)) {				$result = $model->get($id);			}		}else{			$result = $model->get();		}				header('Content-Type: text/json; charset=utf-8');			echo json_encode($result);			exit;	}	function action_tags(){				include PATH_MODL."Model_Tags.php";		$model  = Model_Tags::Instance();			$result = $model->get();				header('Content-Type: text/json; charset=utf-8');			echo json_encode($result);			exit;	}}