<?phpabstract class Controller_Base extends Controller{	protected $title;	protected $content;	protected $error;	protected function before()	{			$this->title = 'Заголовок страници';		$this->content = '';		$this->error = NULL;		// считываем текущее время		$this->start_time = microtime(1);;	}		protected function after()	{			$vars = array('title' => $this->title, 'content' => $this->content, "error"=>$this->error);		$page = $this->View(PATH_VIEW.'v_main.php', $vars);		echo $page;				$page_size = strlen($page);		$comment_size = strlen("<!-- Размер страницы: $page_size байт -->");		$bytes = $page_size + $comment_size;		echo "<!-- Размер страницы: $bytes байт -->";				// считываем текущее время		$this->end_time = microtime(1);		// вычитаем из конечного времени начальное		$time = $this->end_time - $this->start_time;		// выводим время генерации страницы		echo "<!-- Страница сгенерирована за $time секунд -->";			}	}